function onHashChange() {
  let hash = window.location.hash;
  toggle_feature(hash.substring(1));
}

window.addEventListener("hashchange", () => {
  onHashChange();
});
document.addEventListener("DOMContentLoaded", (event) => {
  let hash = window.location.hash;
  if (hash.length === 0) {
    window.location.hash = "feature_input";
  }
  onHashChange();
});
